    CREATE OR REPLACE FUNCTION CALCULATE_AREA (p_radius NUMBER)
    RETURN NUMBER
    as p_area NUMBER;
    
    BEGIN
        p_area := MATH_CONSTANTS.c_Pi * POWER(p_radius, 2);
        RETURN  ROUND(p_area,3);
    END CALCULATE_AREA;
    
